mapper.update:
UPDATE board
SET
    Title = #{title},
    Content = #{content}
WHERE No = #{no}

UPDATE board
SET
    originNo = #{originNo}
WHERE
    No = #{originNo}

UPDATE board
SET Count = Count + 1
WHERE No = #{no}

mapper.select:
SELECT No, Title, Count, Reg_Date as regDate FROM board
WHERE Title LIKE CONCAT('%',#{words},'%') OR Content LIKE CONCAT('%',#{words},'%')
ORDER BY originNo DESC, groupOrd ASC LIMIT #{startPage}, #{endPage}

SELECT groupLayer
FROM board
WHERE No=#{no}

SELECT groupOrd
FROM board
WHERE No=#{no}
